<?php/*Plugin Name: AAM Online Bangla RadioPlugin URI: http://plugins.aamahin.me/aam-online_bangla_radioDescription: This plugin will add a Online Radio Player on your website, by using this sortcode [aambanglaradio].Author: Md. Abdullah Al MahimAuthor URI: http://aamahin.meVersion: 1.0*//*-- Create Sortcode For Online Bangla Radio Station --*/function aam_radio_source( $atts ){?><iframe src="http://www.amarputhia.com/widget/radio_wp.php" width="200" height="95" frameborder="0" scrolling="no"></iframe><?php}add_shortcode('aambanglaradio', 'aam_radio_source');add_filter('widget_text', 'do_shortcode');/*-- Widget For AAM Bangla Radio Player --*/class aamonlineradiowidget extends WP_Widget{  function aamonlineradiowidget()  {    $widget_ops = array('classname' => 'aamonlineradiowidget', 'description' => 'Online Bangla Radio Player widget.' );    $this->WP_Widget('aamonlineradiowidget', 'AAM Online Bangla Radio ', $widget_ops);  }   function form($instance)   {    $instance = wp_parse_args( (array) $instance, array( 'title' => '' ) );    $title = $instance['title'];?>  <p><label for="<?php echo $this->get_field_id('title'); ?>">Title: <input class="widefat" id="<?php echo $this->get_field_id('title'); ?>" name="<?php echo $this->get_field_name('title'); ?>" type="text" value="<?php echo attribute_escape($title); ?>" /></label></p><?php  }   function update($new_instance, $old_instance)  {    $instance = $old_instance;    $instance['title'] = $new_instance['title'];    return $instance;  }   function widget($args, $instance)  {    extract($args, EXTR_SKIP);     //echo $before_widget;    $title = empty($instance['title']) ? ' ' : apply_filters('widget_title', $instance['title']);     if (!empty($title))    echo $before_title . $title . $after_title;;     // WIDGET CODE GOES HERE  ?><iframe src="http://www.amarputhia.com/widget/radio_wp.php" width="200" height="95" frameborder="0" scrolling="no"></iframe> <?php    echo $after_widget;  } }add_action( 'widgets_init', create_function('', 'return register_widget("aamonlineradiowidget");') );?>